mapper.select:
select 
	SOURCE.ID as ID,
	SOURCE.NAME as NAME
from 
	SOURCE, SOURCE_VIEW



			SOURCE_VIEW.FROMID=#{where.fROMID}
			and SOURCE.ID=SOURCE_VIEW.TOID


			SOURCE_VIEW.TOID=#{where.TOID}
			and SOURCE.ID=SOURCE_VIEW.FROMID


	and SOURCE_VIEW.SPACEID=#{where.SPACEID}
	and SOURCE_VIEW.EDGE="true"



		#{offset},


		#{limit}
