mapper.select:
select
	ISOPEN
from 
	SOURCE_NODE_STATE
where
	SOURCEID=#{sourceId}
and SPACEID=#{spaceId}
and	USERID=#{userId}
and TREEID=#{treeId}

select
	count(*)
from 
	SOURCE_NODE_STATE
where
	SOURCEID=#{sourceId}
and SPACEID=#{spaceId}
and	USERID=#{userId}
and TREEID=#{treeId}

mapper.update:
update
	SOURCE_NODE_STATE
set
	ISOPEN=#{isOpen}
where
	SOURCEID=#{sourceId}
and SPACEID=#{spaceId}
and TREEID=#{treeId}
and USERID=#{userId}

		update
        	SOURCE_TREE_STATE
        set
        	CURRENT_SPACEID=#{currentSpaceId}
        where
            TREEID=#{treeId}
        and USERID=#{userId}
